mapper.insert:
INSERT INTO health_code_user.user_info
(uid, name, phone_number, identity_card, district, street, community, address)
VALUES (#{uid}, #{name}, #{phoneNumber}, #{identityCard}, #{district}, #{street}, #{community}, #{address});

mapper.select:
SELECT COUNT(*) > 0
FROM health_code_user.user_info
WHERE uid = #{uid}
